background-color: white;
width: 50vw;
height: 140px;
border: 1px solid #846DB4;
padding: 8px;
border-radius: 20px;
outline: none;
font-size: 15px;
`

const BotaoSalvar = styled.button `
background-color: #846DB4;
color: white;
border: 1px solid #846DB4;
width: 150px;
height: 40px;
font-size: 20px;
margin-top: 25px;
border-radius: 20px;
margin-bottom: 50px;
margin-left: 40vw;
cursor: pointer;
`

const DivFormTemplate = styled.div `
display: flex;
flex-direction: row;
`

const Previa = styled.p `
font-size: 20px;
`

const DivTemplate = styled.div `
