import styled from 'styled-components'

export const Card = styled.div`
    background-color: #C882B4;
    height: 12rem;
    width: 10rem;
    border: none;
    border-radius: 1rem;
    outline: none;
`;

export const ImageDiv = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100%;
`;

export const Image = styled.img`
    height: 3rem;
    width: 2rem;
`;
